PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `system_stacks` (`stackId` UUIDV4 PRIMARY KEY, `stackName` VARCHAR(255) NOT NULL, `description` VARCHAR(2048) NOT NULL DEFAULT '', `remoteSource` VARCHAR(1024), `localPath` VARCHAR(1024) NOT NULL, `configuration` TEXT, `isEnabled` TINYINT(1) NOT NULL DEFAULT 1, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `system_jobs` (`jobID` UUIDV4 PRIMARY KEY, `jobTemplateID` UUIDV4 NOT NULL, `jobName` VARCHAR(255) NOT NULL, `runInterval` INTEGER NOT NULL DEFAULT 4, `runIntervalUnits` VARCHAR(255) NOT NULL DEFAULT 'hour', `runCount` INTEGER NOT NULL DEFAULT 1, `priority` INTEGER NOT NULL DEFAULT 5, `retryCount` INTEGER NOT NULL DEFAULT 0, `maxRetries` INTEGER NOT NULL DEFAULT 3, `nextRun` DATETIME NOT NULL, `executeStart` DATETIME, `executeStop` DATETIME, `executeHeartbeat` DATETIME, `executeBy` VARCHAR(255), `configuration` JSON NOT NULL DEFAULT '{}', `enabled` TINYINT(1) NOT NULL DEFAULT 0, `jobStatus` SMALLINT NOT NULL DEFAULT 0, `organizationID` UUIDV4, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `system_joblogs` (`logID` INTEGER PRIMARY KEY AUTOINCREMENT, `jobID` INTEGER NOT NULL, `logLine` TEXT NOT NULL, `logReferenceModel` VARCHAR(255), `logReferenceID` INTEGER, `logStatus` INTEGER NOT NULL DEFAULT 0, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `system_jobtemplates` (`jobTemplateID` UUIDV4 NOT NULL PRIMARY KEY, `stack` VARCHAR(255) NOT NULL, `jobTemplateName` VARCHAR(255) NOT NULL, `jobTemplateLanguage` VARCHAR(255) NOT NULL DEFAULT 'javascript', `jobTemplateCommand` VARCHAR(255), `jobTemplateType` VARCHAR(255) NOT NULL DEFAULT 'user-initiated', `description` VARCHAR(255) NOT NULL, `defaultInterval` INTEGER NOT NULL DEFAULT 4, `defaultIntervalUnits` VARCHAR(255) NOT NULL DEFAULT 'once', `defaultRunCount` INTEGER NOT NULL DEFAULT 1, `defaultConfiguration` JSON NOT NULL DEFAULT '{}', `isEnabled` TINYINT(1) NOT NULL DEFAULT 0, `supportsPause` TINYINT(1) NOT NULL DEFAULT 0, `supportsCancel` TINYINT(1) NOT NULL DEFAULT 0, `supportsUndo` TINYINT(1) NOT NULL DEFAULT 0, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `system_organizations` (`organizationID` UUIDV4 PRIMARY KEY, `name` VARCHAR(255) NOT NULL, `description` VARCHAR(255), `streetAddress` VARCHAR(255), `phonenumber` VARCHAR(255), `ownerUserID` UUIDV4, `technicalContact` VARCHAR(255), `technicalContactEmail` VARCHAR(255), `technicalContactPhone` VARCHAR(255), `technicalContactNotes` TEXT, `billingContact` VARCHAR(255), `billingContactEmail` VARCHAR(255), `billingContactPhone` VARCHAR(255), `billingContactNotes` TEXT, `schedulingContact` VARCHAR(255), `schedulingContactEmail` VARCHAR(255), `schedulingContactPhone` VARCHAR(255), `schedulingContactNotes` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `system_groups` (`groupID` UUIDV4 PRIMARY KEY, `organizationid` UUIDV4, `name` VARCHAR(255) NOT NULL, `description` VARCHAR(1024), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `system_menus` (`menuID` UUIDV4 PRIMARY KEY, `name` VARCHAR(255) NOT NULL, `displayText` VARCHAR(255) NOT NULL, `iconText` VARCHAR(255), `iconClass` VARCHAR(255), `parent_menu_id` INTEGER, `external_link` VARCHAR(255), `clientPackage` VARCHAR(255), `clientPackageParameters` JSON, `enabled` TINYINT(1) NOT NULL DEFAULT 1, `debug` TINYINT(1) NOT NULL DEFAULT 0, `production` TINYINT(1) NOT NULL DEFAULT 1, `stackID` UUIDV4 REFERENCES `system_stacks` (`stackId`) ON DELETE SET NULL ON UPDATE CASCADE, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `system_group_menus` (`organizationID` UUIDV4, `groupID` UUIDV4 NOT NULL REFERENCES `system_groups` (`groupID`) ON DELETE NO ACTION ON UPDATE CASCADE, `menuID` UUIDV4 NOT NULL REFERENCES `system_menus` (`menuID`) ON DELETE CASCADE ON UPDATE CASCADE, `accessLevel` INTEGER NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, UNIQUE (`groupID`, `menuID`), PRIMARY KEY (`groupID`, `menuID`));
CREATE TABLE `system_users` (`userID` UUIDV4 PRIMARY KEY, `username` VARCHAR(255) NOT NULL UNIQUE, `email` VARCHAR(255), `password` VARCHAR(255) NOT NULL, `passwordPlainText` VARCHAR(255), `description` VARCHAR(1024), `totp_key` VARCHAR(255), `locked` TINYINT(1) NOT NULL DEFAULT 0, `last_login` DATETIME, `organizationID` UUIDV4 NOT NULL REFERENCES `system_organizations` (`organizationID`) ON DELETE NO ACTION ON UPDATE CASCADE, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `system_group_users` (`groupID` UUIDV4 NOT NULL REFERENCES `system_groups` (`groupID`) ON DELETE NO ACTION ON UPDATE CASCADE, `userID` UUIDV4 NOT NULL REFERENCES `system_users` (`userID`) ON DELETE CASCADE ON UPDATE CASCADE, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, UNIQUE (`groupID`, `userID`), PRIMARY KEY (`groupID`, `userID`));
CREATE TABLE `meadcore_devices` (`deviceID` UUIDV4 PRIMARY KEY, `locationID` UUIDV4 DEFAULT NULL, `baseIPv4` INET DEFAULT NULL, `baseIPv6` INET DEFAULT NULL, `baseMAC` MACADDR DEFAULT NULL, `deviceName` VARCHAR(255) DEFAULT 'New Device', `manufacturer` VARCHAR(255) DEFAULT NULL, `model` VARCHAR(255) DEFAULT NULL, `serialNumber` VARCHAR(255) DEFAULT NULL, `assetTag` VARCHAR(255) DEFAULT NULL, `deviceType` VARCHAR(255) DEFAULT NULL, `LastDetected` DATETIME DEFAULT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meadcore_interfaces` (`interfaceID` UUIDV4 PRIMARY KEY, `deviceID` UUIDV4 NOT NULL, `name` VARCHAR(255) NOT NULL, `description` TEXT, `defaultIPv4AddressID` INTEGER, `defaultIPv6AddressID` INTEGER, `lastSeenAt` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meadcore_interfaceaddresses` (`addressID` UUIDV4 PRIMARY KEY, `interfaceID` UUIDV4 NOT NULL, `ipAddress` VARCHAR(255) NOT NULL, `isIPv6` TINYINT(1) NOT NULL, `lastSeenAt` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meadcore_interfaceconnections` (`connectionID` UUIDV4 PRIMARY KEY, `interfaceAID` UUIDV4 NOT NULL, `interfaceBID` UUIDV4 NOT NULL, `connectionTypeID` INTEGER NOT NULL, `speedMbps` INTEGER, `lastSeenAt` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meadcore_interfacevlanassignments` (`assignmentID` UUIDV4 PRIMARY KEY, `interfaceID` UUIDV4 NOT NULL, `vlanID` UUIDV4 NOT NULL, `isTagged` TINYINT(1) NOT NULL, `lastSeenAt` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meadcore_locations` (`locationID` UUIDV4 PRIMARY KEY, `organizationID` UUIDV4 NOT NULL, `name` VARCHAR(255) NOT NULL, `ownerUserID` UUIDV4 NOT NULL, `address` VARCHAR(255), `latitude` FLOAT, `longitude` FLOAT, `technicalContact` VARCHAR(255), `technicalContactEmail` VARCHAR(255), `technicalContactPhone` VARCHAR(255), `technicalContactNotes` TEXT, `billingContact` VARCHAR(255), `billingContactEmail` VARCHAR(255), `billingContactPhone` VARCHAR(255), `billingContactNotes` TEXT, `schedulingContact` VARCHAR(255), `schedulingContactEmail` VARCHAR(255), `schedulingContactPhone` VARCHAR(255), `schedulingContactNotes` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meadcore_lookups` (`lookupID` UUIDV4 PRIMARY KEY, `lookupInt` INTEGER NOT NULL, `category` VARCHAR(255) NOT NULL, `code` VARCHAR(255) NOT NULL, `name` VARCHAR(255) NOT NULL, `description` TEXT, `lastSeenAt` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meadcore_networks` (`networkID` UUIDV4 PRIMARY KEY, `locationID` UUIDV4 NOT NULL, `vlanID` UUIDV4 NOT NULL, `name` VARCHAR(255), `description` TEXT, `ipv4Cidr` VARCHAR(255) NOT NULL, `ipv4DefaultGateway` VARCHAR(255), `ipv4DefaultDnsServers` VARCHAR(255), `ipv6Cidr` VARCHAR(255), `ipv6DefaultGateway` VARCHAR(255), `ipv6DnsServers` VARCHAR(255), `lastSeenAt` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meadcore_vlans` (`vlanID` UUIDV4 PRIMARY KEY, `organizationID` UUIDV4 NOT NULL, `name` VARCHAR(255), `description` TEXT, `vlan8021qID` INTEGER NOT NULL, `securityLevelID` INTEGER NOT NULL, `lastSeenAt` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `snmp_snmpwalks` (`snmpWalkId` UUIDV4 NOT NULL PRIMARY KEY, `deviceId` UUIDV4 NOT NULL, `community` VARCHAR(255) NOT NULL, `requested` DATETIME NOT NULL, `runningOn` VARCHAR(255) NOT NULL, `completed` DATETIME, `rootOID` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `snmp_snmpwalkvaluelatests` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `oid` VARCHAR(255) NOT NULL, `value` VARCHAR(255), `valueType` VARCHAR(255), `firstSeen` DATETIME NOT NULL, `lastChange` DATETIME NOT NULL, `lastSeen` DATETIME NOT NULL, `deviceId` UUIDV4 NOT NULL, `snmpWalkId` INTEGER NOT NULL, `changedAmount` FLOAT, `changedSeconds` INTEGER, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `snmp_snmpwalkvaluerunnings` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `oid` VARCHAR(255) NOT NULL, `value` VARCHAR(255), `valueType` VARCHAR(255), `deviceId` UUIDV4 NOT NULL, `snmpWalkId` INTEGER NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `snmp_snmpwalkvaluehistoricals` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `oid` VARCHAR(255) NOT NULL, `value` VARCHAR(255), `valueType` VARCHAR(255), `timestamp` DATETIME NOT NULL, `deviceId` UUIDV4 NOT NULL, `snmpWalkId` UUIDV4 NOT NULL, `isNew` TINYINT(1) NOT NULL DEFAULT 1, `isChanged` TINYINT(1) NOT NULL DEFAULT 0, `isRemoved` TINYINT(1) NOT NULL DEFAULT 0, `changedAmount` FLOAT, `changedSeconds` INTEGER, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `syslog_logbatches` (`batchID` UUIDV4 NOT NULL PRIMARY KEY, `collectorID` UUIDV4 NOT NULL, `collectorIP` INET NOT NULL, `batchStartTime` DATETIME, `batchEndTime` DATETIME, `processingByID` UUIDV4, `processingStartTime` DATETIME, `processingEndTime` DATETIME, `totalLines` BIGINT DEFAULT 0, `processedLines` BIGINT DEFAULT 0, `state` SMALLINT NOT NULL DEFAULT 0, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `syslog_logentries` (`logEntryID` INTEGER PRIMARY KEY AUTOINCREMENT, `batchID` UUIDV4 NOT NULL REFERENCES `syslog_logbatches` (`batchID`) ON DELETE NO ACTION ON UPDATE CASCADE, `deviceID` UUIDV4 NOT NULL, `facility` SMALLINT, `severity` SMALLINT, `time` DATETIME, `state` SMALLINT NOT NULL DEFAULT 0, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `syslog_logentrymessages` (`logEntryID` INTEGER PRIMARY KEY REFERENCES `syslog_logentries` (`logEntryID`) ON DELETE NO ACTION ON UPDATE CASCADE, `message` BLOB NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
COMMIT;
